<style lang="postcss">
@import '../assets/css/variables.css';

.ec-logo {
  display: block;
  height: 100%;
  padding: 0.75rem;
  border: 2px solid transparent;
  border-radius: var(--border-radius);
  background: transparent;

  &:focus {
